import React from 'react'
import "../contact.css";
import { useRef, useEffect } from 'react';
import emailjs from 'emailjs-com'


function Contact() {

  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('service_bo3s1fq', 'template_dtceju8', form.current, 'W5EkoyNBHpcZA2JJ3')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });

      let popup = document.getElementById("popup");
      let popup_container = document.getElementById("popup_container");
  
      popup.classList.remove("closed");
      popup_container.classList.remove("closed");

      e.target.reset();
  };

  function close_popup(){
    let popup = document.getElementById("popup");
    let popup_container = document.getElementById("popup_container");

    popup.classList.add("closed");
    popup_container.classList.add("closed");
  }


  // ---------------------MOUSE ANIMATION -----------------------------------------------------

    useEffect(() => {
    let canvas2 = document.getElementById('123');
    let ctx2 = canvas2.getContext('2d');
    
    canvas2.width = canvas2.getBoundingClientRect().width;
    canvas2.height = canvas2.getBoundingClientRect().height;
    
    let hue = 1;
    
    
    const particleArray = [];
    
    class Particle {
      constructor(x, y) {
        this.x = x;
        this.y = y;
        this.size = Math.random() * 6 + 1;
        this.speedX = Math.random() * 3 - 1.5;
        this.speedY = Math.random() * 3 - 1.5;
        this.color = hue;
      }
      update() {
        this.x += this.speedX;
        this.y += this.speedY;
        if (this.size > 0.2) this.size -= 0.1;
      }
      draw() {
        ctx2.fillStyle = 'white';
        ctx2.beginPath();
        ctx2.arc(this.x, this.y, this.size, 0, Math.PI * 2);
        ctx2.closePath();
        ctx2.fillStyle= "hsl("+this.color+",100%,50%)";
        ctx2.fill();
    
      }
    }
    
    function init() {
      particleArray.push(new Particle(canvas2.width, canvas2.height));
    }
    
    function animate() {
      ctx2.clearRect(0, 0, canvas2.width, canvas2.height);
      particleArray.forEach((particle, index) => {
        if (particle.size <= 0.2) {
          particleArray.splice(index, 1);
        } else {
          particle.update();
          particle.draw();
        }
      });
      requestAnimationFrame(animate);
    
      hue ++;
    
    }
    
    init();
    animate();


    let offset = 0;
    
    canvas2.addEventListener('mousemove', function(event) {
      //  ON SCROLL: the offset is calculated and added to Y coordinate on MOUSE MOVE
      particleArray.push(new Particle(event.x, event.y + offset));
    });

    window.addEventListener('scroll',function(){
      offset = window.scrollY;
    });

    window.addEventListener('resize',function(){
      canvas2.width = canvas2.getBoundingClientRect().width;
      canvas2.height = canvas2.getBoundingClientRect().height;
    });


  },[]);

  // --------------------- -----------------------------------------------------


  return (
    <div className="canvas_container">

      <div id="123"></div>
      <section id='contact' className="container contact">

      <div class="animation-container">
              <div class="moon">
                  <div class="crater crater-1"></div>
                  <div class="crater crater-2"></div>
                  <div class="crater crater-3"></div>
                  <div class="crater crater-4"></div>
                  <div class="crater crater-5"></div>
                  <div class="shadow"></div>
                  <div class="eye eye-l"></div>
                  <div class="eye eye-r"></div>
                  <div class="mouth"></div>
                  <div class="blush blush-1"></div>
                  <div class="blush blush-2"></div>
              </div>
              <div class="orbit">
                  <div class="rocket">
                      {/* <div class="window"></div> */}
                      <img src="../images/ufo.png" alt="" />
                  </div>
              </div>
      </div>


        <h1>Send me a message</h1>

        <div className="contact_container">

          <div className="contact_options">
            <a href="mailto:nejckovac99@gmail.com" className="contact_option">
              <i class="fa-regular fa-envelope"></i>
              <h4>Email</h4>
              <h5>nejckovac99@gmail.com</h5>
              <button>Contact</button>
            </a>

            <a href="https://m.me/nejc.kovac.14" className="contact_option">
              <i class="fa-brands fa-facebook-messenger"></i>
              <h4>Messenger</h4>
              <h5>Nejc Kovaƒç</h5>
              <button>Contact</button>
            </a>

          </div>

          <form ref={form} onSubmit={sendEmail}>
            <input type="text" name='name' placeholder='Your Full Name' required />
            <input type="email" name='email' placeholder='Your Email' required />
            <textarea name="message" placeholder='message' id=""  rows="10" required></textarea>
            <button type='submit'> Send Message</button>
          </form>

          <div className="popup_container closed" id='popup_container'>
            <div className="popup closed" id='popup'>
              <div className="popup_content">
                <div className="close_btn" onClick={()=>close_popup()}> <i class="fa-solid fa-xmark"></i></div>
                <p>Your message has been sent!</p>
              </div>

            </div>

          </div>
        </div>

      </section>
    </div>
  )
}

export default Contact
